mapper.insert:
 		INSERT INTO BOARD
		( BOARD_NUM, TITLE, CONT, WRITER_NM, WRITE_DATE,
		  REVIEW_CNT, MEMB_NUM, QUES_CD

		  	,HIGH_BOARD_NUM

		  ) VALUES 
		(BOARD_SEQ.NEXTVAL, #{TITLE}, #{CONT}, #{WRITER_NM}, sysdate,
			0, #{MEMB_NUM}, '2202'

		  		,#{HIGH_BOARD_NUM}

		)

mapper.update:
 		UPDATE BOARD
		SET TITLE = #{TITLE},
		    CONT = #{CONT},
		    WRITER_NM = #{WRITER_NM}
		WHERE 
			BOARD_NUM = #{BOARD_NUM}

 		UPDATE BOARD
		SET 
			QUES_CD = #{QUES_CD}			    
		WHERE 
			BOARD_NUM = #{BOARD_NUM}
