mapper.insert:
 		INSERT INTO REG_AUC_FILE
		(
			ATTAC_FILE_NUM, FILE_NM, FILE_PATH, REAL_NM, 
			FILE_SIZE, AUC_REG_NUM
		) values 
		(
			AUC_FILE_SEQ.nextval, #{FILE_NM}, #{FILE_PATH}, #{REAL_NM},  
			#{FILE_SIZE}, #{AUC_REG_NUM}
		)

mapper.update:
 		UPDATE REG_AUC_FILE
		SET 
		   FILE_NM = #{FILE_NM}, 
		   FILE_PATH = #{FILE_PATH}, 
		   REAL_NM = #{REAL_NM}, 
		   FILE_SIZE = #{FILE_SIZE}
		WHERE 
			AUC_REG_NUM = #{AUC_REG_NUM} 
			AND ATTAC_FILE_NUM = #{ATTAC_FILE_NUM}
