mapper.insert:
INSERT INTO COM_USR
( 
	MEMB_NUM, CORP_NM, BUS_NUM, CEO_NM, CHGR_NM
)
VALUES 
(
	#{MEMB_NUM}, #{CORP_NM}, #{BUS_NUM}, #{CEO_NM}, #{CHGR_NM}
)

INSERT INTO PRI_USR
( 
	MEMB_NUM, NM, SOC_REG_NUM
)
VALUES 
(
	#{MEMB_NUM}, #{NM}, #{SOC_REG_NUM}
)

INSERT INTO USR_FILE
( 
	MEMB_NUM, FILE_NM, FILE_PATH, REAL_NM, FILE_SIZE
)
VALUES 
(
	#{MEMB_NUM}, #{FILE_NM}, #{FILE_PATH}, #{REAL_NM}, #{FILE_SIZE}
)

INSERT INTO ELEC_WALLET
( 
	MEMB_NUM, POINT
)
VALUES 
(
	#{MEMB_NUM}, #{POINT}
)

INSERT INTO MEMBERSHIP
( 
	MEMB_NUM, MSHIP_CARD_NUM, NM, 
	POINT, ISSUE_DATE, ISSUE_CNT, DEG
)
VALUES 
(
	#{MEMB_NUM}, #{MSHIP_CARD_NUM}, #{NM}, 
	#{POINT}, SYSDATE, #{ISSUE_CNT}, #{DEG}
)

mapper.select:
SELECT A.MEMB_NUM, A.MEMB_ID, A.SEC_NUM, A.EMAIL, A.TEL_NUM, A.MOBIL_NUM, A.ZIP_NUM, A.ADDRESS, A.DETA_ADDRESS, A.REG_DTIME, A.MEMB_CD, A.WITHDRAWAL_CD, A.CRE_DEG_CD, A.MAIL_CD, B.NM, B.SOC_REG_NUM FROM USR A INNER JOIN PRI_USR B ON A.MEMB_NUM = B.MEMB_NUM AND A.WITHDRAWAL_CD = '1702'
SELECT A.MEMB_NUM, A.MEMB_ID, A.SEC_NUM, A.EMAIL, A.TEL_NUM, A.MOBIL_NUM, A.ZIP_NUM, A.ADDRESS, A.DETA_ADDRESS, A.REG_DTIME, A.MEMB_CD, A.WITHDRAWAL_CD, A.CRE_DEG_CD, A.MAIL_CD, B.CORP_NM, B.BUS_NUM, B.CEO_NM, B.CHGR_NM FROM USR A INNER JOIN COM_USR B ON A.MEMB_NUM = B.MEMB_NUM AND A.WITHDRAWAL_CD = '1702'
WHERE A.MEMB_NUM = #{MEMB_NUM}

mapper.update:
UPDATE PRI_USR
SET 

		SOC_REG_NUM = #{SOC_REG_NUM},

		NM = #{NM}
WHERE MEMB_NUM = #{MEMB_NUM}

UPDATE USR
SET 
	WITHDRAWAL_CD = #{WITHDRAWAL_CD}
WHERE 
	MEMB_NUM = #{MEMB_NUM}
