mapper.select:
SELECT c.PROD_CATE_NUM, c.HIGH_PROD_CATE_NUM, c.PROD_CATE_NM, c.SHORT_CONT, p.PROD_CATE_NM AS PARENT_PROD_CATE_NM, p.PROD_CATE_NUM AS PARENT_PROD_CATE_NUM, p.SHORT_CONT AS PARENT_SHORT_CONT, p.HIGH_PROD_CATE_NUM AS PARENT_HIGH_PROD_CATE_NUM FROM PROD_CATE c LEFT JOIN PROD_CATE p ON p.PROD_CATE_NUM = c.HIGH_PROD_CATE_NUM WHERE
c.USE_CD = '2201' AND c.HIGH_PROD_CATE_NUM = #{HIGH_PROD_CATE_NUM}
START WITH c.PROD_CATE_NUM = '1000' CONNECT BY PRIOR c.PROD_CATE_NUM = c.HIGH_PROD_CATE_NUM;

mapper.insert:
 		insert into PROD_CATE
		(PROD_CATE_NUM, PROD_CATE_NM, SHORT_CONT, USE_CD

		, HIGH_PROD_CATE_NUM

		) values 
		(
		#{PROD_CATE_NUM}, #{PROD_CATE_NM}, #{SHORT_CONT}, '2201'

		, #{HIGH_PROD_CATE_NUM}

		)

mapper.update:
 		update PROD_CATE
		set 
			PROD_CATE_NM = #{PROD_CATE_NM},
		    SHORT_CONT = #{SHORT_CONT}
		where 
			PROD_CATE_NUM = #{PROD_CATE_NUM}

mapper.delete:
  		update PROD_CATE
		set 
			USE_CD = #{USE_CD}
		where 
			PROD_CATE_NUM = #{PROD_CATE_NUM}
